mapper.select:
select a.id,c.name,c.birthday,a.examination_type
examinationType,b.examinationDate,DATEDIFF(CURRENT_DATE,examination_date) overdueDays,a.ding_times dingTimes
from biz_examination_info a,
(select t.baby_id,MAX(t.examination_date) examinationDate from biz_examination_info t
where t.examination_date < CURRENT_DATE group by t.baby_id) b,
biz_baby_info c
where a.baby_id = b.baby_id
and a.examination_date = b.examinationDate
and b.baby_id = c.id
and c.state = 1
and a.sign_in = 0
and c.corpid = #{corpid}

    and DATEDIFF(CURRENT_DATE,examination_date) > #{param.overdueStart,jdbcType=INTEGER}


    and DATEDIFF(CURRENT_DATE,examination_date) <= #{param.overdueEnd,jdbcType=INTEGER}


    and a.examination_type = #{param.examinationType,jdbcType=INTEGER}


    and a.ding_times = #{param.dingTimes,jdbcType=INTEGER}

order by a.ding_times desc,a.examination_type
limit #{offset},#{size}
