mapper.insert:
insert into biz_dues_standard (id, org_id, type,job,
  money, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{orgId,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT},#{job,jdbcType=VARCHAR},
  #{money,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id,org_id,type,(select `NAME` from t_dict where id=job) job,money,create_time
from biz_dues_standard
where org_id = #{orgId,jdbcType=VARCHAR}

    and `type` = #{type,jdbcType=TINYINT}


    and job = #{job,jdbcType=VARCHAR}

order by type,create_time desc

    LIMIT
    #{currentPageIndex,jdbcType=INTEGER},
    #{pageSize,jdbcType=INTEGER}
